import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        while (scanner.hasNextLine()) {
            String[] line = scanner.nextLine().split(" ");
            int[] a = new int[4];
            int[] b = new int[4];

            for (int i = 0; i < 4; i++) {
                a[i] = Integer.parseInt(line[i * 2]);
                b[i] = Integer.parseInt(line[i * 2 + 1]);
            }

            int hit = 0;
            int blow = 0;

            for (int i = 0; i < 4; i++) {
                if (a[i] == b[i]) {
                    hit++;
                } else {
                    for (int j = 0; j < 4; j++) {
                        if (a[j] == b[i]) {
                            blow++;
                            break;
                        }
                    }
                }
            }

            System.out.println(hit + " " + (blow - hit));
        }

        scanner.close();
    }
}
