#include <iostream>
#include <vector>
#include <cmath>

using namespace std;

struct Point {
    double x, y;
};

Point reflectX(Point p, vector<pair<int, int>> mirrors) {
    Point result = p;
    for (auto mirror : mirrors) {
        if (mirror.first == p.x && mirror.second == p.y - 1) {
            result.x = mirror.first;
            result.y = mirror.second + 1;
        }
    }
    return result;
}

Point reflectY(Point p, vector<pair<int, int>> mirrors) {
    Point result = p;
    for (auto mirror : mirrors) {
        if (mirror.first == p.x - 1 && mirror.second == p.y) {
            result.x = mirror.first + 1;
            result.y = mirror.second;
        }
    }
    return result;
}

Point findReflection(Point thief, vector<pair<int, int>> mirrors) {
    Point reflection = thief;
    for (auto mirror : mirrors) {
        if (mirror.first == thief.x && mirror.second == thief.y - 1) {
            reflection = reflectX(reflection, mirrors);
        } else if (mirror.first == thief.x - 1 && mirror.second == thief.y) {
            reflection = reflectY(reflection, mirrors);
        }
    }
    return reflection;
}

int main() {
    int n;
    cin >> n;
    vector<pair<int, int>> mirrors;
    while (n != -1) {
        for (int i = 0; i < n; i++) {
            char direction;
            int x, y;
            cin >> direction >> x >> y;
            mirrors.push_back({x, y});
        }
        Point thief = {0.75, 0.25};
        Point reflection = findReflection(thief, mirrors);
        cout << (int)(reflection.x * 100) << " " << (int)(reflection.y * 100) << endl;
        mirrors.clear();
        cin >> n;
    }
    return 0;
}
