#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define N 10000
#define EPS 1e-5

typedef struct {
    double x, y;
} point;

double distance(point a, point b) {
    return sqrt(pow(a.x - b.x, 2) + pow(a.y - b.y, 2));
}

double prob(point ic, point pc, point acm) {
    double icd = 0, pcd = 0, acmd = 0;
    for (int i = 0; i < 10000; i++) {
        for (int j = 0; j < 10000; j++) {
            point p = {i, j};
            double d1 = distance(p, ic);
            double d2 = distance(p, pc);
            double d3 = distance(p, acm);
            if (d1 <= d2 && d1 <= d3) icd++;
            if (d2 <= d1 && d2 <= d3) pcd++;
            if (d3 <= d1 && d3 <= d2) acmd++;
        }
    }
    double icp = (icd - pcd) / (double) (N * N);
    double icac = (icd - acmd) / (double) (N * N);
    return (icp + icac) / 2;
}

int main() {
    int t = 0;
    while (1) {
        int x1, y1, x2, y2, x3, y3;
        scanf("%d %d %d %d %d %d", &x1, &y1, &x2, &y2, &x3, &y3);
        if (x1 == 0 && y1 == 0 && x2 == 0 && y2 == 0 && x3 == 0 && y3 == 0) break;
        t++;
        point ic = {x1, y1};
        point pc = {x2, y2};
        point acm = {x3, y3};
        double p = prob(ic, pc, acm);
        printf("%d %.5f\n", t, p);
    }
    return 0;
}
