#include <iostream>
#include <queue>
#include <vector>
#include <limits>

using namespace std;

const int INF = numeric_limits<int>::max();

struct Point {
    int x, y, time, hp;
};

int main() {
    int w, h;
    while (cin >> w >> h) {
        if (w == 0 && h == 0) break;

        vector<vector<int>> map(h, vector<int>(w));
        for (int i = 0; i < h; i++) {
            for (int j = 0; j < w; j++) {
                cin >> map[i][j];
            }
        }

        queue<Point> q;
        vector<vector<bool>> visited(h, vector<bool>(w, false));
        int start_x, start_y;
        for (int i = 0; i < h; i++) {
            for (int j = 0; j < w; j++) {
                if (map[i][j] == 2) {
                    start_x = j;
                    start_y = i;
                    break;
                }
            }
        }

        q.push({start_x, start_y, 0, 6});
        visited[start_y][start_x] = true;

        int min_time = INF;
        while (!q.empty()) {
            Point p = q.front();
            q.pop();

            if (map[p.y][p.x] == 3 && p.hp > 0) {
                min_time = min(min_time, p.time);
            }

            for (int dx = -1; dx <= 1; dx++) {
                for (int dy = -1; dy <= 1; dy++) {
                    int nx = p.x + dx;
                    int ny = p.y + dy;

                    if (nx >= 0 && nx < w && ny >= 0 && ny < h && map[ny][nx] != 0 && map[ny][nx] != 4 && !visited[ny][nx]) {
                        q.push({nx, ny, p.time + 1, p.hp - 1});
                        visited[ny][nx] = true;
                    }
                }
            }

            if (p.hp > 0) {
                q.push({p.x, p.y, p.time + 1, 6});
                visited[p.y][p.x] = true;
            }
        }

        if (min_time == INF) {
            cout << -1 << endl;
        } else {
            cout << min_time << endl;
        }
    }

    return 0;
}
