from collections import deque

def bfs(grid, start):
    rows, cols = len(grid), len(grid[0])
    directions = [(0, 1), (0, -1), (1, 0), (-1, 0)]
    queue = deque([(start, 0, 6)])
    visited = {(start[0], start[1], 6)}
    while queue:
        x, y, hp = queue.popleft()
        if grid[x][y] == 3:
            return hp
        for dx, dy in directions:
            nx, ny = x + dx, y + dy
            if 0 <= nx < rows and 0 <= ny < cols and grid[nx][ny] != 0 and (nx, ny, hp - 1) not in visited:
                if grid[nx][ny] == 4:
                    queue.append((nx, ny, 6))
                    visited.add((nx, ny, 6))
                else:
                    queue.append((nx, ny, hp - 1))
                    visited.add((nx, ny, hp - 1))
    return -1

def solve():
    while True:
        try:
            rows, cols = map(int, input().split())
            grid = []
            for _ in range(rows):
                grid.append(list(map(int, input().split())))
            for i in range(rows):
                for j in range(cols):
                    if grid[i][j] == 2:
                        print(bfs(grid, (i, j)))
                        return
        except ValueError:
            break

solve()
