#include <stdio.h>
#include <string.h>
#include <stdbool.h>

#define MAXN 1000000
#define MAXK 50
#define MAXS 72

int n, k;
char s[MAXN + 1];
char key[MAXK + 1];

void solve() {
    int i, j, cnt, min_len, min_idx;
    bool found;

    scanf("%s", s);
    scanf("%s", key);
    scanf("\n");

    min_len = MAXN;
    min_idx = -1;
    found = false;

    for (i = 0; i <= n - k; i++) {
        cnt = 0;
        for (j = 0; j < k; j++) {
            if (s[i + j] == key[j]) {
                cnt++;
            }
        }
        if (cnt == k) {
            if (i + k - 1 < min_len) {
                min_len = i + k - 1;
                min_idx = i;
            }
            found = true;
        }
    }

    if (found) {
        printf("%d\n\n", 1);
        for (i = min_idx; i <= min_idx + k - 1; i++) {
            printf("%c", s[i]);
            if (i == min_idx + k - 1) {
                printf("\n");
            } else if (i % MAXS == MAXS - 1) {
                printf("\n");
            }
        }
        printf("\n");
    } else {
        printf("0\n\n");
    }
}

int main() {
    while (scanf("%s", s) == 1 && s[0] != '\0') {
        n = strlen(s);
        while (scanf("%s", s) == 1 && s[0] != '\0') {
            if (s[0] == '\n') {
                continue;
            }
            if (scanf("%s", key) == 1) {
                solve();
            }
            scanf("\n");
        }
    }
    return 0;
}
