#include <iostream>
#include <fstream>
#include <string>
#include <vector>
#include <algorithm>
#include <set>
#include <map>

using namespace std;

int main() {
    ifstream fin("input.txt");
    ofstream fout("output.txt");

    string s;
    map<char, int> k;
    int p = 0;

    while (getline(fin, s)) {
        if (s.empty()) {
            if (p > 0) {
                p = 0;
                for (auto& c : k) {
                    c.second = 0;
                }
            }
            continue;
        }

        if (p == 0) {
            p = 1;
            continue;
        }

        if (p == 1) {
            for (char c : s) {
                k[c]++;
            }
            p = 2;
            continue;
        }

        int n = s.size();
        int l = 0;
        int r = 0;
        int min_len = n + 1;
        int min_r = -1;
        int cnt = 0;

        while (r < n) {
            if (k.find(s[r]) != k.end() && k[s[r]] > 0) {
                cnt++;
            }
            k[s[r]]--;

            while (cnt == k.size()) {
                if (r - l + 1 < min_len) {
                    min_len = r - l + 1;
                    min_r = l;
                }
                if (k.find(s[l]) != k.end() && k[s[l]] >= 0) {
                    cnt--;
                }
                k[s[l]]++;
                l++;
            }
            r++;
        }

        if (min_r != -1) {
            fout << min_len << endl;
            for (int i = min_r; i < min_r + min_len; i++) {
                fout << s[i] << endl;
            }
            fout << endl;
        } else {
            fout << 0 << endl;
            fout << endl;
        }

        p = 1;
    }

    return 0;
}
