import java.io.BufferedReader;
import java.io.BufferedWriter;
import java.io.FileReader;
import java.io.FileWriter;
import java.io.IOException;
import java.util.*;

public class Main {
    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new FileReader("input.txt"));
        BufferedWriter bw = new BufferedWriter(new FileWriter("output.txt"));

        String line;
        int p = 0;
        while ((line = br.readLine()) != null) {
            if (line.isEmpty()) {
                p++;
                continue;
            }
            if (p == 0) {
                continue;
            }

            String s = line;
            line = br.readLine();
            Set<Character> k = new HashSet<>();
            for (char c : line.toCharArray()) {
                k.add(c);
            }

            int n = s.length();
            int[] last = new int[256];
            for (int i = 0; i < 256; i++) {
                last[i] = -1;
            }
            for (int i = 0; i < n; i++) {
                last[s.charAt(i)] = i;
            }

            int min = Integer.MAX_VALUE;
            for (char c : k) {
                min = Math.min(min, last[c]);
            }

            if (min == -1) {
                bw.write("0\n\n");
                continue;
            }

            int l = min;
            int r = min;
            int len = 1;
            for (int i = 0; i < n; i++) {
                if (k.contains(s.charAt(i))) {
                    r = i;
                    len++;
                } else {
                    len = 1;
                    l = i;
                    r = i;
                }
                if (len == k.size()) {
                    break;
                }
            }

            if (len < k.size()) {
                bw.write("0\n\n");
                continue;
            }

            int minLen = Integer.MAX_VALUE;
            for (int i = l; i <= r; i++) {
                minLen = Math.min(minLen, r - i + 1);
            }

            int count = 0;
            for (int i = l; i <= r; i++) {
                if (r - i + 1 == minLen) {
                    count++;
                    bw.write(s.substring(i, r + 1) + "\n");
                }
            }

            bw.write(count + "\n\n");
        }

        br.close();
        bw.close();
    }
}
