from collections import defaultdict
import sys

def read_input():
    with open('input.txt', 'r') as f:
        lines = f.readlines()
    return lines

def solve(lines):
    n = len(lines)
    i = 0
    while i < n:
        s = ''
        k = set()
        while i < n and lines[i].strip():
            s += lines[i].strip()
            k.update(lines[i].strip())
            i += 1
        i += 1  # skip empty line

        if not k:
            continue

        min_len = float('inf')
        min_str = ''
        for j in range(len(s)):
            for c in k:
                if s[j] != c:
                    break
            else:
                if j + 1 < min_len:
                    min_len = j + 1
                    min_str = s[j - min_len + 1:j + 1]
                break

        if min_len == float('inf'):
            print(0)
            print()
            continue

        print(min_len)
        print()
        for i in range(0, len(min_str), 72):
            print(min_str[i:i+72])
        print()

def main():
    lines = read_input()
    solve(lines)

if __name__ == '__main__':
    main()
