#include <stdio.h>
#include <math.h>

#define EPS 0.0001

typedef struct {
    double x, y, z;
} point;

double distance(point p1, point p2) {
    return sqrt(pow(p1.x - p2.x, 2) + pow(p1.y - p2.y, 2) + pow(p1.z - p2.z, 2));
}

int main() {
    int t;
    scanf("%d", &t);

    while (t--) {
        double a, b, c;
        scanf("%lf %lf %lf", &a, &b, &c);

        int n;
        scanf("%d", &n);

        point p[n + 1];
        for (int i = 1; i <= n; i++) {
            scanf("%lf %lf %lf", &p[i].x, &p[i].y, &p[i].z);
        }

        double sa = a, sb = b, sc = c;
        int pa = 1, pb = 2, pc = 3;

        for (int i = 1; i <= n; i++) {
            for (int j = i + 1; j <= n; j++) {
                for (int k = j + 1; k <= n; k++) {
                    double da = distance(p[i], p[j]), db = distance(p[j], p[k]), dc = distance(p[k], p[i]);
                    double ratio = (da / db + db / dc + dc / da) / 3;

                    if (fabs(ratio - 2) < EPS && fabs((da * da + db * db + dc * dc) / (sa * sa + sb * sb + sc * sc) - 1) < EPS) {
                        if (da < sb + sc && db < sa + sc && dc < sa + sb) {
                            if (fabs((da / sb + sb / db + db / dc) / 3 - 2) < EPS && fabs((da * da + db * db + dc * dc) / (sa * sa + sb * sb + sc * sc) - 1) < EPS) {
                                if (fabs((da / sc + sc / db + db / dc) / 3 - 2) < EPS && fabs((da * da + db * db + dc * dc) / (sa * sa + sb * sb + sc * sc) - 1) < EPS) {
                                    if (fabs((da / sa + sa / db + db / dc) / 3 - 2) < EPS && fabs((da * da + db * db + dc * dc) / (sa * sa + sb * sb + sc * sc) - 1) < EPS) {
                                        pa = i; pb = j; pc = k;
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }

        printf("%d %d %d\n", pa, pb, pc);
    }

    return 0;
}
