#include <iostream>
#include <vector>
#include <cmath>
#include <algorithm>

struct Point {
    double x, y, z;
};

double distance(const Point& p1, const Point& p2) {
    return std::sqrt(std::pow(p1.x - p2.x, 2) + std::pow(p1.y - p2.y, 2) + std::pow(p1.z - p2.z, 2));
}

double ratio(const Point& p1, const Point& p2, const Point& p3, double a, double b, double c) {
    return (distance(p1, p2) / distance(p2, p3)) * (a / b);
}

int main() {
    int t;
    std::cin >> t;
    while (t--) {
        double a, b, c;
        std::cin >> a >> b >> c;
        int n;
        std::cin >> n;
        std::vector<Point> points(n);
        for (int i = 0; i < n; ++i) {
            std::cin >> points[i].x >> points[i].y >> points[i].z;
        }
        double maxRatio = 0;
        int p1, p2, p3;
        for (int i = 0; i < n; ++i) {
            for (int j = i + 1; j < n; ++j) {
                for (int k = j + 1; k < n; ++k) {
                    double ratio1 = ratio(points[i], points[j], points[k], a, b, c);
                    double ratio2 = ratio(points[j], points[k], points[i], b, c, a);
                    double ratio3 = ratio(points[k], points[i], points[j], c, a, b);
                    if (std::abs(ratio1 - 1) < 0.0001 && std::abs(ratio2 - 1) < 0.0001 && std::abs(ratio3 - 1) < 0.0001) {
                        if (ratio1 > maxRatio) {
                            maxRatio = ratio1;
                            p1 = i, p2 = j, p3 = k;
                        }
                    }
                }
            }
        }
        std::cout << p1 + 1 << " " << p2 + 1 << " " << p3 + 1 << std::endl;
    }
    return 0;
}
