import math

def distance(p1, p2):
    return math.sqrt((p1[0] - p2[0])**2 + (p1[1] - p2[1])**2 + (p1[2] - p2[2])**2)

def is_similar(triangle, original):
    a, b, c = sorted([distance(triangle[0], triangle[1]), distance(triangle[1], triangle[2]), distance(triangle[2], triangle[0])])
    d, e, f = sorted([original[0], original[1], original[2]])
    return math.isclose(a / b, d / e, rel_tol=1e-4) and math.isclose(b / c, e / f, rel_tol=1e-4) and math.isclose(c / a, f / d, rel_tol=1e-4)

def find_similar_triangle(points, original):
    for i in range(len(points)):
        for j in range(i + 1, len(points)):
            for k in range(j + 1, len(points)):
                if is_similar([points[i], points[j], points[k]], original):
                    return [i + 1, j + 1, k + 1]
    return None

def main():
    num_data_sets = int(input())
    for _ in range(num_data_sets):
        original = list(map(float, input().split()))
        num_points = int(input())
        points = []
        for _ in range(num_points):
            points.append(list(map(float, input().split())))
        similar_triangle = find_similar_triangle(points, original)
        if similar_triangle:
            print(*similar_triangle)
        else:
            print("No similar triangle found")

if __name__ == "__main__":
    main()
