#include <stdio.h>
#include <stdlib.h>

#define MAX_DROPS 50
#define MAX_SIZE 3
#define MAX_X 10
#define MAX_Y 10

int main() {
    int drops[MAX_DROPS][3];
    int density[MAX_X][MAX_Y] = {{0}};
    int numDrops = 0;

    while (numDrops < MAX_DROPS && 3 == scanf("%d,%d,%d", &drops[numDrops][0], &drops[numDrops][1], &drops[numDrops][2])) {
        int x = drops[numDrops][0];
        int y = drops[numDrops][1];
        int size = drops[numDrops][2];

        if (0 <= x && x < MAX_X && 0 <= y && y < MAX_Y) {
            for (int i = -size; i <= size; i++) {
                for (int j = -size; j <= size; j++) {
                    int nx = x + i;
                    int ny = y + j;

                    if (0 <= nx && nx < MAX_X && 0 <= ny && ny < MAX_Y) {
                        density[nx][ny] += size;
                    }
                }
            }
        }

        numDrops++;
    }

    int zeroCells = 0;
    int maxDensity = 0;

    for (int i = 0; i < MAX_X; i++) {
        for (int j = 0; j < MAX_Y; j++) {
            if (density[i][j] == 0) {
                zeroCells++;
            }
            maxDensity = (maxDensity > density[i][j]) ? maxDensity : density[i][j];
        }
    }

    printf("%d\n", zeroCells);
    printf("%d\n", maxDensity);

    return 0;
}
