#include <iostream>
#include <queue>
#include <vector>
#include <utility>

using namespace std;

const int dx[4] = {0, 1, 0, -1};
const int dy[4] = {1, 0, -1, 0};

struct Node {
    int x, y, step, man;
};

int main() {
    int w, h;
    while (cin >> w >> h) {
        if (w == 0 && h == 0) break;
        vector<vector<int>> map(h, vector<int>(w));
        for (int i = 0; i < h; i++) {
            for (int j = 0; j < w; j++) {
                cin >> map[i][j];
            }
        }
        int cargoX, cargoY, goalX, goalY;
        for (int i = 0; i < h; i++) {
            for (int j = 0; j < w; j++) {
                if (map[i][j] == 2) {
                    cargoX = j;
                    cargoY = i;
                } else if (map[i][j] == 3) {
                    goalX = j;
                    goalY = i;
                } else if (map[i][j] == 4) {
                    manX = j;
                    manY = i;
                }
            }
        }
        queue<Node> q;
        q.push({cargoX, cargoY, 0, -1});
        vector<vector<bool>> visited(h, vector<bool>(w, false));
        visited[cargoY][cargoX] = true;
        while (!q.empty()) {
            Node node = q.front();
            q.pop();
            if (node.x == goalX && node.y == goalY) {
                cout << node.step << endl;
                break;
            }
            for (int i = 0; i < 4; i++) {
                int newX = node.x + dx[i];
                int newY = node.y + dy[i];
                if (newX >= 0 && newX < w && newY >= 0 && newY < h && map[newY][newX] != 1 && !visited[newY][newX]) {
                    q.push({newX, newY, node.step + 1, node.man});
                    visited[newY][newX] = true;
                }
            }
            if (node.man != -1) {
                for (int i = 0; i < 4; i++) {
                    int newX = node.man + dx[i];
                    int newY = node.man + dy[i];
                    if (newX >= 0 && newX < w && newY >= 0 && newY < h && map[newY][newX] != 1 && !visited[newY][newX]) {
                        q.push({newX, newY, node.step + 1, node.man});
                        visited[newY][newX] = true;
                    }
                }
            }
        }
        if (q.empty()) {
            cout << -1 << endl;
        }
    }
    return 0;
}
