import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.*;

public class Main {
    static class Pair {
        int x, y;

        public Pair(int x, int y) {
            this.x = x;
            this.y = y;
        }
    }

    static int[][] dirs = {{0, 1}, {0, -1}, {1, 0}, {-1, 0}};
    static int[][] map;
    static int w, h;
    static int cargoX, cargoY;
    static int goalX, goalY;
    static int manX, manY;
    static boolean[][] visited;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        String[] input;
        while ((input = br.readLine().split(" ")) != null && input.length > 0) {
            w = Integer.parseInt(input[0]);
            h = Integer.parseInt(input[1]);
            map = new int[h][w];
            cargoX = -1;
            cargoY = -1;
            goalX = -1;
            goalY = -1;
            manX = -1;
            manY = -1;
            for (int i = 0; i < h; i++) {
                input = br.readLine().split(" ");
                for (int j = 0; j < w; j++) {
                    map[i][j] = Integer.parseInt(input[j]);
                    if (map[i][j] == 2) {
                        cargoX = i;
                        cargoY = j;
                    } else if (map[i][j] == 3) {
                        goalX = i;
                        goalY = j;
                    } else if (map[i][j] == 4) {
                        manX = i;
                        manY = j;
                    }
                }
            }
            if (cargoX == -1 || goalX == -1 || manX == -1) {
                System.out.println(-1);
                continue;
            }
            visited = new boolean[h][w];
            Queue<Pair> queue = new LinkedList<>();
            queue.add(new Pair(manX, manY));
            visited[manX][manY] = true;
            int steps = 0;
            while (!queue.isEmpty()) {
                int size = queue.size();
                for (int i = 0; i < size; i++) {
                    Pair pair = queue.poll();
                    if (pair.x == cargoX && pair.y == cargoY) {
                        System.out.println(steps);
                        break;
                    }
                    for (int[] dir : dirs) {
                        int newX = pair.x + dir[0];
                        int newY = pair.y + dir[1];
                        if (newX >= 0 && newX < h && newY >= 0 && newY < w && map[newX][newY] != 1 && !visited[newX][newY]) {
                            queue.add(new Pair(newX, newY));
                            visited[newX][newY] = true;
                        }
                    }
                }
                steps++;
            }
            if (steps == h * w) {
                System.out.println(-1);
            }
        }
    }
}
