from collections import deque

def push(grid):
    h, w = len(grid), len(grid[0])
    directions = [(0, 1), (0, -1), (1, 0), (-1, 0)]
    queue = deque([(0, 0, 0, 0, 0)])  # x, y, steps, cargo, man
    visited = {(0, 0)}

    while queue:
        x, y, steps, cargo, man = queue.popleft()

        if grid[x][y] == 3:
            return steps

        for dx, dy in directions:
            nx, ny = x + dx, y + dy

            if 0 <= nx < h and 0 <= ny < w and (nx, ny) not in visited:
                if grid[nx][ny] == 0:
                    queue.append((nx, ny, steps + 1, cargo, man))
                    visited.add((nx, ny))
                elif grid[nx][ny] == 1:
                    if (x + dx, y) in visited or (x, y + dy) in visited:
                        queue.append((nx, ny, steps + 1, cargo, man))
                        visited.add((nx, ny))
                elif grid[nx][ny] == 2:
                    if cargo == 0:
                        queue.append((nx, ny, steps + 1, 1, man))
                        visited.add((nx, ny))
                elif grid[nx][ny] == 4:
                    if man == 0:
                        queue.append((nx, ny, steps + 1, cargo, 1))
                        visited.add((nx, ny))

    return -1

def main():
    while True:
        w, h = map(int, input().split())
        if w == 0 and h == 0:
            break

        grid = []
        for _ in range(h):
            grid.append(list(map(int, input().split())))

        print(push(grid))

if __name__ == "__main__":
    main()
