import java.util.*;
import java.io.*;

public class Main {
    static class Guard {
        int id;
        int[] pattern;
        int time;
        int chargingTime;
        int waitingTime;
        boolean isCharging;
        boolean isWaiting;

        public Guard(int id, int[] pattern) {
            this.id = id;
            this.pattern = pattern;
            this.time = 0;
            this.chargingTime = 0;
            this.waitingTime = 0;
            this.isCharging = false;
            this.isWaiting = false;
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);

        String[] input = br.readLine().split(" ");
        int numGuards = Integer.parseInt(input[0]);
        int duration = Integer.parseInt(input[1]);

        List<Guard> guards = new ArrayList<>();
        for (int i = 0; i < numGuards; i++) {
            int[] pattern = Arrays.stream(br.readLine().split(" ")).mapToInt(Integer::parseInt).toArray();
            guards.add(new Guard(i + 1, pattern));
        }

        br.readLine();

        int totalWaitingTime = 0;
        while (duration-- > 0) {
            for (Guard g : guards) {
                g.time++;
                if (g.time % (g.pattern.length / 2) == 0) {
                    g.isCharging = true;
                    g.isWaiting = false;
                    g.chargingTime = g.pattern[g.time % (g.pattern.length / 2) * 2];
                } else {
                    g.isCharging = false;
                    g.isWaiting = true;
                    g.waitingTime++;
                }
            }

            PriorityQueue<Guard> queue = new PriorityQueue<>((a, b) -> a.id - b.id);
            for (Guard g : guards) {
                if (g.isWaiting) {
                    queue.add(g);
                }
            }

            if (!queue.isEmpty()) {
                Guard g = queue.poll();
                g.isCharging = true;
                g.isWaiting = false;
                g.chargingTime = g.pattern[g.time % (g.pattern.length / 2) * 2];
                totalWaitingTime += g.waitingTime;
            }
        }

        pw.println(totalWaitingTime);
        pw.close();
    }
}
