import heapq

def simulate_charging(guards, duration):
    charger = []
    time = 0
    total_wait_time = 0

    for i in range(duration):
        while charger and charger[0] <= i:
            heapq.heappop(charger)

        for j, (consume, charge) in enumerate(guards):
            if (time + consume) % (consume + charge) == consume:
                if not charger or charger[0] > i:
                    heapq.heappush(charger, i + charge)
                else:
                    total_wait_time += i - charger[0]
                    heapq.heappush(charger, i + charge)

        time += 1

    return total_wait_time

def main():
    while True:
        try:
            num_guards, duration = map(int, input().split())
            if num_guards == 0 and duration == 0:
                break

            guards = []
            for _ in range(num_guards):
                pattern = list(map(int, input().split()))
                while pattern and pattern[-1] == 0:
                    pattern.pop()
                guards.append(pattern)

            print(simulate_charging(guards, duration))
        except ValueError:
            break

if __name__ == "__main__":
    main()
