#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

struct Segment {
    int row, col, len;
    char c;
};

int main() {
    int T;
    cin >> T;
    while (T--) {
        string s;
        cin >> s;
        int n = s.size();
        vector<Segment> segments;
        segments.push_back({n, 0, 1, s[0]});
        int cursorRow = n, cursorCol = 0;
        for (int i = 1; i < n; i++) {
            char c = s[i];
            if (c >= 'a' && c <= 'z') {
                segments.back().len++;
                segments.back().c = c;
                cursorCol++;
            } else if (c == 'C') {
                if (cursorCol == segments.back().len - 1) {
                    cout << "ERROR" << endl;
                    goto next_session;
                }
                segments.push_back({cursorRow, cursorCol + 1, 1, segments[cursorRow].c});
                cursorCol++;
            } else if (c == 'D') {
                if (cursorCol == segments.back().len - 1) {
                    cout << "ERROR" << endl;
                    goto next_session;
                }
                segments.back().len--;
                cursorCol++;
            } else if (c == 'B') {
                if (cursorCol == 0) {
                    cout << "ERROR" << endl;
                    goto next_session;
                }
                cursorCol--;
            } else if (c == 'F') {
                if (cursorCol == segments.back().len - 1) {
                    cout << "ERROR" << endl;
                    goto next_session;
                }
                cursorCol++;
            } else if (c == 'N') {
                if (cursorRow == 0) {
                    cout << "ERROR" << endl;
                    goto next_session;
                }
                cursorRow--;
            } else if (c == 'P') {
                if (cursorRow == 0) {
                    cout << "ERROR" << endl;
                    goto next_session;
                }
                cursorRow++;
            }
            // apply gravity
            for (int i = cursorRow; i > 0; i--) {
                bool empty = true;
                for (int j = 0; j < segments[i].len; j++) {
                    bool found = false;
                    for (int k = 0; k < segments[i - 1].len; k++) {
                        if (segments[i - 1].c == segments[i].c + j) {
                            found = true;
                            break;
                        }
                    }
                    if (!found) {
                        empty = false;
                        break;
                    }
                }
                if (empty) {
                    for (int j = 0; j < segments[i].len; j++) {
                        segments[i - 1].c = segments[i].c + j;
                    }
                    segments[i].row--;
                    segments[i].len = 0;
                }
            }
            // apply magnetic force
            for (int i = 0; i < segments.size() - 1; i++) {
                if (segments[i].row == segments[i + 1].row && segments[i].col + segments[i].len == segments[i + 1].col) {
                    segments[i].len += segments[i + 1].len;
                    segments[i].c = segments[i].c + segments[i].len - 1;
                    segments.erase(segments.begin() + i + 1);
                    i--;
                }
            }
        }
        if (segments.empty()) {
            cout << "ERROR" << endl;
        } else {
            for (int i = 0; i < segments[0].len; i++) {
                cout << segments[0].c;
            }
            cout << endl;
        }
        next_session:;
    }
    return 0;
}
