#include <iostream>
#include <vector>
#include <queue>
#include <algorithm>

using namespace std;

struct Node {
    int value;
    int parent;
    int child;
    int num;
};

int max(int a, int b) {
    return a > b ? a : b;
}

int min(int a, int b) {
    return a < b ? a : b;
}

int nextOdd(int n) {
    n++;
    while (n % 2 == 0) {
        n++;
    }
    return n;
}

int prevOdd(int n) {
    n--;
    while (n % 2 == 0) {
        n--;
    }
    return n;
}

int main() {
    vector<int> values;
    int value;
    while (true) {
        cin >> value;
        if (value == 0) {
            break;
        }
        values.push_back(value);
    }

    for (int i = 0; i < values.size(); i++) {
        int maxNum = 1;
        int maxVal = values[i];
        int lifeTime = 0;
        vector<Node> nodes;
        nodes.push_back({values[i], -1, -1, 1});
        while (true) {
            lifeTime++;
            vector<Node> newNodes;
            for (int j = 0; j < nodes.size(); j++) {
                int n = nodes[j].value;
                int p = nodes[j].parent;
                int c = nodes[j].child;
                int num = nodes[j].num;
                if (n == 1) {
                    if (p != -1) {
                        newNodes.push_back({1, p, c, 0});
                        nodes[p].child = c;
                        nodes[c].parent = p;
                    }
                    continue;
                }
                int next = 3 * n + 1;
                while (next % 2 == 0) {
                    next /= 2;
                }
                if (next > 12345678) {
                    next -= 12345678;
                }
                newNodes.push_back({next, p, c, num});
                nodes.push_back({next, p, c, num});
                if (next == 1) {
                    continue;
                }
                int child = nextOdd((next + 1) / 2);
                newNodes.push_back({child, p, c, num + 1});
                nodes.push_back({child, p, c, num + 1});
                if (p != -1) {
                    nodes[p].child = c;
                    nodes[c].parent = p;
                }
                if (c != -1) {
                    nodes[c].parent = p;
                }
            }
            nodes = newNodes;
            int newMax = 0;
            for (int j = 0; j < nodes.size(); j++) {
                if (nodes[j].num > newMax) {
                    newMax = nodes[j].num;
                }
            }
            if (newMax > maxNum) {
                maxNum = newMax;
                maxVal = nodes[newMax - 1].value;
            }
            if (maxNum == 1) {
                break;
            }
        }
        cout << lifeTime << " " << maxNum << endl;
    }
    return 0;
}
