import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        int[][] density = new int[10][10];
        Scanner scanner = new Scanner(System.in);
        int count = 0;

        while (scanner.hasNextLine()) {
            String[] input = scanner.nextLine().split(",");
            int x = Integer.parseInt(input[0]);
            int y = Integer.parseInt(input[1]);
            int size = Integer.parseInt(input[2]);

            for (int i = Math.max(0, y - size); i <= Math.min(9, y + size); i++) {
                for (int j = Math.max(0, x - size); j <= Math.min(9, x + size); j++) {
                    density[i][j] += size;
                    if (density[i][j] > 5) {
                        density[i][j] = 5;
                    }
                }
            }
            count++;
        }

        int zeroCount = 0;
        int maxDensity = 0;
        for (int i = 0; i < 10; i++) {
            for (int j = 0; j < 10; j++) {
                if (density[i][j] == 0) {
                    zeroCount++;
                }
                maxDensity = Math.max(maxDensity, density[i][j]);
            }
        }

        System.out.println(zeroCount);
        System.out.println(maxDensity);
    }
}
