#include <stdio.h>
#include <math.h>

#define N 41
#define M 21

double max_area[N][M];
double p[N];

void init() {
    for (int i = 0; i < N; i++) {
        for (int j = 0; j < M; j++) {
            max_area[i][j] = -1.0;
        }
    }
}

double area(int n, int m, double p[]) {
    if (m == 1) return 0.0;
    if (m == 2) return 0.5 * sin(2 * M_PI * (p[n - 1] - p[0]));
    if (m == 3) return 0.5 * sin(2 * M_PI * (p[n - 1] - p[0])) + 0.5 * sin(2 * M_PI * (p[1] - p[0])) + 0.5 * sin(2 * M_PI * (p[n - 1] - p[1]));
    if (max_area[n][m] != -1.0) return max_area[n][m];
    double max = 0.0;
    for (int i = 0; i < n - 1; i++) {
        double area = area(n - 1, m - 1, p) + 0.5 * sin(2 * M_PI * (p[i + 1] - p[i]));
        if (area > max) max = area;
    }
    max_area[n][m] = max;
    return max;
}

int main() {
    int n, m;
    while (scanf("%d %d", &n, &m) == 2) {
        for (int i = 0; i < n; i++) {
            scanf("%lf", &p[i]);
        }
        printf("%.6f\n", area(n, m, p));
    }
    return 0;
}
