#include <iostream>
#include <vector>
#include <cmath>
#include <algorithm>

using namespace std;

double crossProduct(double x1, double y1, double x2, double y2) {
    return x1 * y2 - x2 * y1;
}

double calculateArea(vector<double> points, int m) {
    double maxArea = 0.0;
    for (int i = 0; i < points.size() - m + 1; i++) {
        vector<double> polygon;
        for (int j = 0; j < m; j++) {
            polygon.push_back(points[(i + j) % points.size()]);
        }
        double area = 0.0;
        for (int j = 0; j < m; j++) {
            double x1 = cos(2 * M_PI * polygon[j]);
            double y1 = sin(2 * M_PI * polygon[j]);
            double x2 = cos(2 * M_PI * polygon[(j + 1) % m]);
            double y2 = sin(2 * M_PI * polygon[(j + 1) % m]);
            double x3 = cos(2 * M_PI * polygon[(j + 2) % m]);
            double y3 = sin(2 * M_PI * polygon[(j + 2) % m]);
            area += crossProduct(x2 - x1, y2 - y1, x3 - x2, y3 - y2);
        }
        area = abs(area) / 2.0;
        maxArea = max(maxArea, area);
    }
    return maxArea;
}

int main() {
    int n, m;
    while (cin >> n >> m) {
        if (n == 0 && m == 0) break;
        vector<double> points(n);
        for (int i = 0; i < n; i++) {
            cin >> points[i];
        }
        sort(points.begin(), points.end());
        double maxArea = calculateArea(points, m);
        cout << fixed << setprecision(6) << maxArea << endl;
    }
    return 0;
}
