import java.util.Arrays;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            int n = scanner.nextInt();
            int m = scanner.nextInt();
            double[] p = new double[n];
            for (int i = 0; i < n; i++) {
                p[i] = scanner.nextDouble();
            }
            if (n == 0 && m == 0) {
                break;
            }
            double maxArea = 0;
            for (int i = 0; i < n; i++) {
                double[] q = new double[n];
                for (int j = 0; j < n; j++) {
                    q[j] = (p[(i + j) % n] + p[(i + j + 1) % n]) / 2;
                }
                double[] areas = new double[n];
                for (int j = 0; j < n; j++) {
                    double area = 0;
                    for (int k = 0; k < n; k++) {
                        if (k != j) {
                            double alpha = Math.abs(q[j] - q[k]);
                            if (alpha > Math.PI) {
                                alpha = 2 * Math.PI - alpha;
                            }
                            area += Math.sin(alpha);
                        }
                    }
                    areas[j] = area / 2;
                }
                Arrays.sort(areas);
                double area = 0;
                for (int j = 0; j < m; j++) {
                    area += areas[n - 1 - j];
                }
                maxArea = Math.max(maxArea, area);
            }
            System.out.printf("%.6f%n", maxArea);
        }
    }
}
