#include <stdio.h>
#include <math.h>

int is_cubic(int n) {
    int i = 1;
    while (i * i * i <= n) {
        if (i * i * i == n) return 1;
        i++;
    }
    return 0;
}

int is_tetrahedral(int n) {
    int i = 1;
    while (i * (i + 1) * (i + 2) / 6 <= n) {
        if (i * (i + 1) * (i + 2) / 6 == n) return 1;
        i++;
    }
    return 0;
}

