import math

def is_cubic(n):
    return n ** (1. / 3.) == int(n ** (1. / 3.))

def is_tetrahedral(n):
    n_ = int(math.sqrt(24 * n + 1))
    return n_ ** 2 - 24 * n == 1

def find_cubic(n):
    i = 0
    while i ** 3 <= n:
        i += 1
    return i - 1

def find_tetrahedral(n):
    i = 0
    while i * (i + 1) * (i + 2) // 6 <= n:
        i += 1
    return i - 1

def solve(n):
    cubic = find_cubic(n)
    tetrahedral = find_tetrahedral(n)
    for i in range(cubic + 1):
        for j in range(tetrahedral + 1):
            if i ** 3 + j * (j + 1) * (j + 2) // 6 == n:
                return n
    return n - 1

with open('input.txt', 'r') as f:
    for line in f:
        n = int(line.strip())
        if n == 0:
            break
        print(solve(n))
