#include <stdio.h>
#include <math.h>

int main() {
    int n;
    double a[31], b[31], c[31], d[31];

    while (scanf("%d", &n) == 1) {
        if (n == 0) break;

        for (int i = 0; i < n; i++) {
            scanf("%lf", &a[i]);
        }
        for (int i = 0; i < n; i++) {
            scanf("%lf", &b[i]);
        }
        for (int i = 0; i < n; i++) {
            scanf("%lf", &c[i]);
        }
        for (int i = 0; i < n; i++) {
            scanf("%lf", &d[i]);
        }

        double max = 0.0;
        for (int i = 0; i < n; i++) {
            double x1 = a[i];
            double x2 = b[i];
            double y1 = c[i];
            double y2 = d[i];

            double dx = x2 - x1;
            double dy = y2 - y1;

            double dist = sqrt(dx * dx + dy * dy);

            double mesh1 = (x2 - x1) / n;
            double mesh2 = (y2 - y1) / n;

            double mesh = (mesh1 < mesh2) ? mesh1 : mesh2;

            max = (max > mesh) ? max : mesh;
        }

        printf("%.6lf\n", max);
    }

    return 0;
}
