#include <iostream>
#include <vector>
#include <cmath>
#include <iomanip>

using namespace std;

double calculateMeshSize(vector<double> a, vector<double> b, vector<double> c, vector<double> d) {
    double minDistance = 1.0;
    for (int i = 0; i < a.size(); i++) {
        double distance = abs((b[i] - a[i]) * (c[0] - c[i]) / (d[0] - d[i]));
        minDistance = min(minDistance, distance);
    }
    return minDistance;
}

int main() {
    int n;
    while (cin >> n) {
        if (n == 0) break;

        vector<double> a(n), b(n), c(n), d(n);
        for (int i = 0; i < n; i++) cin >> a[i];
        for (int i = 0; i < n; i++) cin >> b[i];
        for (int i = 0; i < n; i++) cin >> c[i];
        for (int i = 0; i < n; i++) cin >> d[i];

        double meshSize = calculateMeshSize(a, b, c, d);
        cout << fixed << setprecision(6) << meshSize << endl;
    }

    return 0;
}
