import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            int n = scanner.nextInt();
            double[] a = new double[n];
            double[] b = new double[n];
            double[] c = new double[n];
            double[] d = new double[n];

            for (int i = 0; i < n; i++) {
                a[i] = scanner.nextDouble();
            }
            for (int i = 0; i < n; i++) {
                b[i] = scanner.nextDouble();
            }
            for (int i = 0; i < n; i++) {
                c[i] = scanner.nextDouble();
            }
            for (int i = 0; i < n; i++) {
                d[i] = scanner.nextDouble();
            }

            double max = 0;
            for (int i = 0; i < n; i++) {
                double dist1 = Math.sqrt(Math.pow(a[i] - b[i], 2) + Math.pow(1, 2));
                double dist2 = Math.sqrt(Math.pow(c[i] - d[i], 2) + Math.pow(a[i] - 1, 2));
                double dist3 = Math.sqrt(Math.pow(c[i] - d[i], 2) + Math.pow(0, 2));
                double dist4 = Math.sqrt(Math.pow(a[i] - b[i], 2) + Math.pow(c[i] - 1, 2));
                double dist5 = Math.sqrt(Math.pow(a[i] - b[i], 2) + Math.pow(d[i] - 1, 2));
                double dist6 = Math.sqrt(Math.pow(c[i] - d[i], 2) + Math.pow(b[i] - 1, 2));
                double dist7 = Math.sqrt(Math.pow(c[i] - d[i], 2) + Math.pow(b[i] - 0, 2));
                double dist8 = Math.sqrt(Math.pow(a[i] - b[i], 2) + Math.pow(d[i] - 0, 2));

                double dist = Math.min(Math.min(dist1, dist2), Math.min(dist3, Math.min(dist4, Math.min(dist5, Math.min(dist6, Math.min(dist7, dist8))))));

                max = Math.max(max, dist);
            }

            System.out.printf("%.6f\n", max);
            if (scanner.nextInt() == 0) {
                break;
            }
        }
    }
}
