import math

def calculate_distance(x1, y1, x2, y2):
    return math.sqrt((x2 - x1)**2 + (y2 - y1)**2)

def calculate_mesh_size(n, a, b, c, d):
    min_distance = float('inf')
    for i in range(n):
        distance1 = calculate_distance(a[i], 0, b[i], 1)
        distance2 = calculate_distance(c[i], 0, d[i], 1)
        min_distance = min(min_distance, distance1, distance2)
    return min_distance

def main():
    while True:
        n = int(input())
        a = list(map(float, input().split()))
        b = list(map(float, input().split()))
        c = list(map(float, input().split()))
        d = list(map(float, input().split()))
        print(f"{calculate_mesh_size(n, a, b, c, d):.6f}")
        if n == 0:
            break

if __name__ == "__main__":
    main()
