#include <iostream>
#include <vector>
#include <string>
#include <unordered_map>

using namespace std;

void solve() {
    int n;
    cin >> n;
    vector<string> words(n);
    for (int i = 0; i < n; i++) {
        cin >> words[i];
    }
    string sequence;
    cin >> sequence;
    unordered_map<string, vector<string>> dp;
    dp[""].push_back("");
    for (char c : sequence) {
        unordered_map<string, vector<string>> next;
        for (auto& pair : dp) {
            string word = pair.first;
            vector<string> list = pair.second;
            for (int i = 0; i < 4; i++) {
                for (int j = 0; j < 4; j++) {
                    for (int k = 0; k < 4; k++) {
                        for (int l = 0; l < 4; l++) {
                            string nextWord = word + words[i * 4 + j] + words[4 * (k + 1) + l];
                            if (nextWord.length() <= sequence.length()) {
                                next[nextWord].push_back(nextWord);
                            }
                        }
                    }
                }
            }
        }
        dp = next;
    }
    for (auto& pair : dp) {
        if (!pair.second.empty()) {
            for (int i = 0; i < pair.second.size(); i++) {
                cout << pair.second[i];
                if (i < pair.second.size() - 1) {
                    cout << " ";
                } else {
                    cout << "." << endl;
                }
            }
            cout << "--" << endl;
            return;
        }
    }
    cout << "--" << endl;
}

int main() {
    int t;
    cin >> t;
    while (t--) {
        solve();
    }
    return 0;
}
