import java.util.*;
import java.io.*;

public class Main {
    static Map<String, String> dict = new HashMap<>();
    static Map<String, List<String>> dp = new HashMap<>();

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        int T = Integer.parseInt(br.readLine());
        while (T-- > 0) {
            solve(br);
        }
    }

    static void solve(BufferedReader br) throws IOException {
        int n = Integer.parseInt(br.readLine());
        for (int i = 0; i < n; i++) {
            String word = br.readLine();
            dict.put(word, word);
        }
        String sequence = br.readLine();
        dp.clear();
        dfs(sequence, "");
        for (String s : dp.keySet()) {
            System.out.print(s + ".");
            for (String word : dp.get(s)) {
                System.out.print(" " + word);
            }
            System.out.println();
        }
        System.out.println("--");
    }

    static void dfs(String sequence, String path) {
        if (sequence.isEmpty()) {
            if (dict.containsKey(path)) {
                dp.computeIfAbsent(path, k -> new ArrayList<>()).add(dict.get(path));
            }
            return;
        }
        for (int i = 1; i <= 4; i++) {
            if (sequence.length() >= i) {
                dfs(sequence.substring(i), path + getChar(sequence.charAt(0) - '0', i));
            }
        }
    }

    static String getChar(int digit, int index) {
        String[] map = {"abc", "def", "ghi", "jkl", "mno", "pqrs", "tuv", "wxyz"};
        return map[digit - 2].charAt(index - 1) + "";
    }
}
