from collections import defaultdict
from itertools import permutations

def get_letters(num):
    letters = {
        '2': 'abc',
        '3': 'def',
        '4': 'ghi',
        '5': 'jkl',
        '6': 'mno',
        '7': 'pqrs',
        '8': 'tuv',
        '9': 'wxyz'
    }
    return letters[num]

def get_words(num):
    words = {}
    for i in range(1, 10):
        words[i] = get_letters(str(i))
    return words

def get_permutations(word):
    return [''.join(p) for p in permutations(word)]

def solve():
    n = int(input())
    words = []
    for _ in range(n):
        words.append(input())
    sequence = input()
    words_dict = defaultdict(list)
    for word in words:
        for p in get_permutations(word):
            words_dict[p].append(word)
    result = []
    for p in get_permutations(sequence):
        if p in words_dict:
            result.append(' '.join(words_dict[p]) + '.')
    print('\n'.join(result))
    print('--')

t = 1
for _ in range(t):
    solve()
