#include <stdio.h>
#include <string.h>

int main() {
    int n;
    scanf("%d", &n);

    while (n--) {
        char s1[101], s2[101];
        scanf("%s %s", s1, s2);
        getchar(); // Consume the newline character
        scanf("%c", &s2[strlen(s2) - 1]); // Consume the hyphen

        int len1 = strlen(s1);
        int len2 = strlen(s2);

        if (len1 != len2) {
            printf("false\n");
            continue;
        }

        int i;
        for (i = 0; i < len1; i++) {
            if (s1[i] != s2[i]) {
                break;
            }
        }

        if (i == len1) {
            printf("true\n");
        } else {
            int j;
            for (j = 0; j < len1; j++) {
                if (s1[j] != s2[(len1 - i + j) % len1]) {
                    break;
                }
            }

            if (j == len1) {
                printf("true\n");
            } else {
                printf("false\n");
            }
        }
    }

    return 0;
}
