#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

bool isSameLayout(const std::string& s1, const std::string& s2) {
    if (s1.length() != s2.length()) return false;
    std::vector<int> v1(6, 0), v2(6, 0);
    for (int i = 0; i < s1.length(); ++i) {
        switch (s1[i]) {
            case 'a': v1[0] = 1; break;
            case 'b': v1[1] = 1; break;
            case 'c': v1[2] = 1; break;
            case 'd': v1[3] = 1; break;
            case 'e': v1[4] = 1; break;
            case 'f': v1[5] = 1; break;
        }
        switch (s2[i]) {
            case 'a': v2[0] = 1; break;
            case 'b': v2[1] = 1; break;
            case 'c': v2[2] = 1; break;
            case 'd': v2[3] = 1; break;
            case 'e': v2[4] = 1; break;
            case 'f': v2[5] = 1; break;
        }
    }
    return v1 == v2;
}

int main() {
    int n;
    std::cin >> n;
    for (int i = 0; i < n; ++i) {
        std::string s1, s2, s3;
        std::cin >> s1 >> s2 >> s3;
        std::cout << (isSameLayout(s1, s2) ? "true" : "false") << std::endl;
    }
    return 0;
}
