import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        scanner.nextLine(); // Consume newline left-over

        for (int i = 0; i < n; i++) {
            String s1 = scanner.nextLine();
            String s2 = scanner.nextLine();
            scanner.nextLine(); // Consume the hyphen

            String s3 = scanner.nextLine();
            String s4 = scanner.nextLine();

            System.out.println(isSameLayout(s1, s2, s3, s4));
        }
    }

    public static String isSameLayout(String s1, String s2, String s3, String s4) {
        if (isRotation(s1, s3)) {
            return isRotation(s2, s4) ? "true" : "false";
        } else {
            return isRotation(s2, s3) ? isRotation(s1, s4) ? "true" : "false" : "false";
        }
    }

    public static boolean isRotation(String s1, String s2) {
        if (s1.length() != s2.length()) {
            return false;
        }

        int n = s1.length();
        for (int i = 0; i < n; i++) {
            if (s1.substring(i, n) + s1.substring(0, i).equals(s2)) {
                return true;
            }
        }
        return false;
    }
}
