import java.util.Arrays;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            int n = scanner.nextInt();
            if (n == 0) {
                break;
            }
            double[][] points = new double[n][3];
            for (int i = 0; i < n; i++) {
                for (int j = 0; j < 3; j++) {
                    points[i][j] = scanner.nextDouble();
                }
            }
            double[] center = getCenter(points);
            double radius = getRadius(points, center);
            System.out.printf("%.5f%n", radius);
        }
    }

    private static double[] getCenter(double[][] points) {
        double[] center = new double[3];
        for (int i = 0; i < 3; i++) {
            for (double[] point : points) {
                center[i] += point[i];
            }
            center[i] /= points.length;
        }
        return center;
    }

    private static double getRadius(double[][] points, double[] center) {
        double maxDistance = 0;
        for (double[] point : points) {
            double distance = Math.sqrt(Math.pow(point[0] - center[0], 2) + Math.pow(point[1] - center[1], 2) + Math.pow(point[2] - center[2], 2));
            maxDistance = Math.max(maxDistance, distance);
        }
        return maxDistance;
    }
}
