        int p = 2, q = 2;
        for (int i = 2; i * i <= m; i++) {
            if (is_prime(i) && is_prime(m / i)) {
                if (a * 1.0 / b <= i * 1.0 / (m / i) && i * 1.0 / (m / i) <= 1) {
                    if (i * (m / i) > p * q) {
                        p = i;
                        q = m / i;
                    }
                }
            }
        }
        printf("%d %d\n", p, q);
    }
    return 0;
}
