#include <iostream>
#include <vector>
#include <cmath>
#include <algorithm>

bool isPrime(int n) {
    if (n <= 1) return false;
    if (n == 2) return true;
    if (n % 2 == 0) return false;
    for (int i = 3; i <= std::sqrt(n); i += 2) {
        if (n % i == 0) return false;
    }
    return true;
}

int main() {
    while (true) {
        int m, a, b;
        std::cin >> m >> a >> b;
        if (m == 0 && a == 0 && b == 0) break;

        int p = 0, q = 0;
        for (int i = 2; i <= m; ++i) {
            if (isPrime(i)) {
                for (int j = 2; j <= m; ++j) {
                    if (isPrime(j)) {
                        if (a * j <= b * i && i / j >= a / b && i / j <= 1) {
                            if (i * j > p * q) {
                                p = i;
                                q = j;
                            }
                        }
                    }
                }
            }
        }

        std::cout << p << " " << q << std::endl;
    }

    return 0;
}
