import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            int m = scanner.nextInt();
            int a = scanner.nextInt();
            int b = scanner.nextInt();
            if (m == 0 && a == 0 && b == 0) {
                break;
            }
            int p = 0;
            int q = 0;
            for (int i = 2; i <= m; i++) {
                if (isPrime(i)) {
                    for (int j = 2; j <= m; j++) {
                        if (isPrime(j)) {
                            if (i * j <= m && a * 1.0 / b <= i * 1.0 / j && i * 1.0 / j <= 1) {
                                if (i * j > p * q) {
                                    p = i;
                                    q = j;
                                }
                            }
                        }
                    }
                }
            }
            System.out.println(p + " " + q);
        }
    }

    public static boolean isPrime(int n) {
        if (n <= 1) {
            return false;
        }
        for (int i = 2; i * i <= n; i++) {
            if (n % i == 0) {
                return false;
            }
        }
        return true;
    }
}
