def is_prime(n):
    if n < 2:
        return False
    for i in range(2, int(n**0.5) + 1):
        if n % i == 0:
            return False
    return True

def find_prime_factors(n):
    factors = []
    i = 2
    while i * i <= n:
        if n % i:
            i += 1
        else:
            n //= i
            factors.append(i)
    if n > 1:
        factors.append(n)
    return factors

def find_max_area(m, a, b):
    max_area = 0
    p, q = 0, 0
    for i in range(2, m + 1):
        if is_prime(i):
            for j in range(2, m + 1):
                if is_prime(j) and i * j <= m and a / b <= i / j <= 1:
                    area = i * j
                    if area > max_area:
                        max_area = area
                        p, q = i, j
    return p, q

