import re
import sympy as sp

def parse_expression(expression):
    expression = re.sub(r'\s+', '', expression)
    expression = re.sub(r'([a-z])\^([0-9])', r'\1\2', expression)
    return expression

def evaluate_expression(expression):
    x = sp.symbols('x')
    for var in 'abcdefghijklmnopqrstuvwxyz':
        expression = expression.replace(var, f'x.{var}')
    return sp.sympify(expression)

def main():
    while True:
        correct_expression = input()
        if correct_expression == '.':
            break
        correct_expression = parse_expression(correct_expression)
        for _ in range(10):
            expression = input()
            if expression == '.':
                print('.')
                break
            expression = parse_expression(expression)
            try:
                if evaluate_expression(expression) == evaluate_expression(correct_expression):
                    print('yes')
                else:
                    print('no')
            except Exception:
                print('no')

if __name__ == '__main__':
    main()
