import numpy as np
from scipy.stats import binom

def calculate_probability(matches):
    teams = [team for team in matches[0] if team != '_']
    team_index = teams.index('*' + teams[-1])
    team = teams[team_index]
    team_index = teams.index(team)

    results = []
    for match in matches[1:]:
        match_results = []
        for i in range(4):
