                    match_results.append((0, 0))
                else:
                    match_results.append((int(match[i*4 + j][2]) - int(match[i*4 + j][5]), int(match[i*4 + j][2]) + int(match[i*4 + j][5])))
        results.append(match_results)

    points = [0] * 4
    goals = [0] * 4
    goal_diff = [0] * 4
    for i in range(4):
        for j in range(i + 1, 4):
            if results[team_index][i][0] > results[team_index][j][0]:
                points[team_index] += 3
            elif results[team_index][i][0] < results[team_index][j][0]:
                points[j] += 3
            else:
                if results[team_index][i][1] > results[team_index][j][1]:
                    points[team_index] += 1
                elif results[team_index][i][1] < results[team_index][j][1]:
                    points[j] += 1
                else:
                    points[team_index] += 1
                    points[j] += 1
            goals[team_index] += results[team_index][i][1]
            goals[j] += results[team_index][j][1]
            goal_diff[team_index] += results[team_index][i][0] - results[team_index][j][0]
            goal_diff[j] += results[team_index][j][0] - results[team_index][i][0]

    teams_to_qualify = []
    for i in range(4):
        if points[i] >= 4:
            teams_to_qualify.append(i)

    if len(teams_to_qualify) == 2:
        team1 = teams_to_qualify[0]
        team2 = teams_to_qualify[1]
        if points[team1] > points[team2]:
            return 1.0
        elif points[team1] < points[team2]:
            return 0.0
        else:
            if goal_diff[team1] > goal_diff[team2]:
                return 1.0
            elif goal_diff[team1] < goal_diff[team2]:
                return 0.0
            else:
                if goals[team1] > goals[team2]:
                    return 1.0
                elif goals[team1] < goals[team2]:
                    return 0.0
                else:
                    team1_matches = []
                    team2_matches = []
                    for i in range(4):
                        for j in range(i + 1, 4):
                            if results[team1][i][0] > results[team1][j][0]:
                                team1_matches.append((results[team1][i][1], results[team1][j][1]))
                            elif results[team1][i][0] < results[team1][j][0]:
                                team2_matches.append((results[team1][i][1], results[team1][j][1]))
                            else:
                                if results[team1][i][1] > results[team1][j][1]:
                                    team1_matches.append((results[team1][i][1], results[team1][j][1]))
                                elif results[team1][i][1] < results[team1][j][1]:
                                    team2_matches.append((results[team1][i][1], results[team1][j][1]))
                    team1_matches.sort(key=lambda x: x[0], reverse=True)
                    team2_matches.sort(key=lambda x: x[0], reverse=True)
                    for i in range(len(team1_matches)):
                        if team1_matches[i][0] > team2_matches[i][0]:
                            return 1.0
                        elif team1_matches[i][0] < team2_matches[i][0]:
                            return 0.0
    return 0.5

n = int(input())
for _ in range(n):
