#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int dfs(vector<vector<int>>& board, int i, int j, int c) {
    if (i < 0 || i >= board.size() || j < 0 || j >= board[0].size() || board[i][j] == 0) {
        return 0;
    }
    if (board[i][j] == c) {
        int count = 1;
        board[i][j] = 0;
        for (int x = -1; x <= 1; x++) {
            for (int y = -1; y <= 1; y++) {
                if (x == 0 && y == 0) continue;
                count += dfs(board, i + x, j + y, c);
            }
        }
        return count;
    }
    return 0;
}

int maxPoints(vector<vector<int>>& board, int c) {
    int maxPoints = 0;
    for (int i = 0; i < board.size(); i++) {
        for (int j = 0; j < board[0].size(); j++) {
            if (board[i][j] == 0) {
                int points = 0;
                for (int x = -1; x <= 1; x++) {
                    for (int y = -1; y <= 1; y++) {
                        if (x == 0 && y == 0) continue;
                        int count = dfs(board, i + x, j + y, board[i + x][j + y]);
                        if (count > 0) {
                            points += count;
                        }
                    }
                }
                maxPoints = max(maxPoints, points);
            }
        }
    }
    return maxPoints;
}

int main() {
    int n, c;
    while (cin >> n >> c) {
        vector<vector<int>> board(n, vector<int>(n));
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                cin >> board[i][j];
            }
        }
        cout << maxPoints(board, c) << endl;
    }
    return 0;
}
