from datetime import datetime

def get_day_of_week(m, d):
    date = datetime(2004, m, d)
    return date.strftime("%A")

def main():
    while True:
        line = input()
        if line == "0 0":
            break
        m, d = map(int, line.split())
        print(get_day_of_week(m, d))

if __name__ == "__main__":
    main()
