import java.util.Scanner;

public class Main {
    static int[][] board;
    static int n, c;

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (scanner.hasNextLine()) {
            String[] line = scanner.nextLine().split(" ");
            if (line.length == 0) {
                continue;
            }
            n = Integer.parseInt(line[0]);
            c = Integer.parseInt(line[1]);
            board = new int[n][n];
            for (int i = 0; i < n; i++) {
                String[] row = scanner.nextLine().split(" ");
                for (int j = 0; j < n; j++) {
                    board[i][j] = Integer.parseInt(row[j]);
                }
            }
            System.out.println(maxPoints());
        }
    }

    static int maxPoints() {
        int max = 0;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                if (board[i][j] == 0) {
                    int points = 0;
                    for (int k = -1; k <= 1; k++) {
                        for (int l = -1; l <= 1; l++) {
                            int x = i + k;
                            int y = j + l;
                            if (x >= 0 && x < n && y >= 0 && y < n) {
                                if (board[x][y] != 0 && board[x][y] != c) {
                                    points++;
                                }
                            }
                        }
                    }
                    for (int k = -1; k <= 1; k++) {
                        for (int l = -1; l <= 1; l++) {
                            int x = i + k;
                            int y = j + l;
                            if (x >= 0 && x < n && y >= 0 && y < n) {
                                if (board[x][y] == 0) {
                                    int group = 0;
                                    for (int m = -1; m <= 1; m++) {
                                        for (int n = -1; n <= 1; n++) {
                                            int a = x + m;
                                            int b = y + n;
                                            if (a >= 0 && a < n && b >= 0 && b < n) {
                                                if (board[a][b] != 0 && board[a][b] == board[x][y]) {
                                                    group++;
                                                }
                                            }
                                        }
                                    }
                                    if (group == 0) {
                                        points--;
                                    }
                                }
                            }
                        }
                    }
                    max = Math.max(max, points);
                }
            }
        }
        return max;
    }
}
