#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 110
#define MAXM 4100

int n, m, cnt;
int head[MAXN], to[MAXM], nxt[MAXM], val[MAXM];
int vis[MAXN], d[MAXN], id[MAXN], num[MAXN];
int tot;

void add(int x, int y, int z) {
    to[++tot] = y;
    val[tot] = z;
    nxt[tot] = head[x];
    head[x] = tot;
}

void dfs(int x) {
    vis[x] = 1;
    for (int i = head[x]; i; i = nxt[i]) {
        int y = to[i];
        if (!vis[y]) {
            d[y] = d[x] + 1;
            id[y] = ++cnt;
            num[cnt] = val[i];
            add(y, x, 1);
            dfs(y);
        } else {
            add(y, x, 1);
        }
    }
}

int main() {
    int T;
    scanf("%d", &T);
    while (T--) {
        scanf("%d", &n);
        cnt = 0;
        memset(head, 0, sizeof(head));
        memset(vis, 0, sizeof(vis));
        memset(d, 0, sizeof(d));
        memset(id, 0, sizeof(id));
        memset(num, 0, sizeof(num));
        memset(val, 0, sizeof(val));
        memset(nxt, 0, sizeof(nxt));
        memset(to, 0, sizeof(to));
        tot = 0;
        for (int i = 1; i <= n; i++) {
            int x;
            while (scanf("%d", &x) == 1 && x != 0) {
                if (x > 0) {
                    add(++cnt, id[i], x);
                } else {
                    add(id[i], id[i - 1], -x);
                }
            }
        }
        dfs(1);
        for (int i = 1; i <= cnt; i++) {
            int x = id[i];
            int y = head[x];
            printf("%d", x);
            for (; y; y = nxt[y]) {
                int z = to[y];
                if (z != id[i - 1]) {
                    printf(" %d", z);
                }
            }
            printf(" %d\n", num[i]);
        }
    }
    return 0;
}
