#include <iostream>
#include <vector>
#include <string>
#include <sstream>

using namespace std;

int main() {
    int n;
    cin >> n;

    for (int i = 0; i < n; i++) {
        vector<int> rooms;
        int room = 1;
        int prevRoom = 0;
        int count = 0;
        string line;
        getline(cin, line);
        stringstream ss(line);
        int num;
        while (ss >> num) {
            if (num > 0) {
                rooms.push_back(num);
                count = 0;
                for (int j = 0; j < rooms.size(); j++) {
                    if (rooms[j] == num) {
                        count++;
                    }
                }
                for (int j = 0; j < rooms.size(); j++) {
                    if (rooms[j] == num) {
                        rooms[j] = room;
                    }
                }
                room++;
            } else {
                int diff = prevRoom - room;
                for (int j = 0; j < rooms.size(); j++) {
                    if (rooms[j] == room) {
                        rooms[j] = prevRoom;
                    }
                }
                room = prevRoom;
                prevRoom += diff;
                rooms.push_back(-diff);
            }
        }

        vector<vector<int>> graph(rooms.size());
        for (int j = 0; j < rooms.size(); j++) {
            for (int k = 0; k < rooms.size(); k++) {
                if (rooms[j] == k + 1) {
                    graph[j].push_back(k + 1);
                }
            }
        }

        for (int j = 0; j < graph.size(); j++) {
            cout << j + 1;
            for (int k = 0; k < graph[j].size(); k++) {
                cout << " " << graph[j][k];
            }
            cout << " " << graph[j].size() << endl;
        }
    }

    return 0;
}
