import java.util.*;
import java.io.*;

public class Main {
    static class Graph {
        int V;
        List<List<Integer>> adj;

        public Graph(int V) {
            this.V = V;
            this.adj = new ArrayList<>();
            for (int i = 0; i < V; i++) {
                this.adj.add(new ArrayList<>());
            }
        }

        public void addEdge(int u, int v) {
            this.adj.get(u).add(v);
            this.adj.get(v).add(u);
        }

        public void printGraph() {
            for (int i = 0; i < this.V; i++) {
                System.out.print("Adjacency list of vertex " + i + ": ");
                for (int j = 0; j < this.adj.get(i).size(); j++) {
                    System.out.print(this.adj.get(i).get(j) + " ");
                }
                System.out.println();
            }
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        int n = Integer.parseInt(br.readLine());
        for (int i = 0; i < n; i++) {
            String[] input = br.readLine().split(" ");
            int[] rooms = new int[input.length];
            for (int j = 0; j < input.length; j++) {
                rooms[j] = Integer.parseInt(input[j]);
            }
            Graph g = new Graph(rooms.length);
            int currRoom = 0;
            int prevRoom = -1;
            int count = 0;
            for (int j = 0; j < rooms.length; j++) {
                if (rooms[j] > 0) {
                    g.addEdge(currRoom, count);
                    count++;
                    currRoom++;
                } else {
                    g.addEdge(currRoom, prevRoom);
                    currRoom--;
                    prevRoom = currRoom;
                    g.addEdge(currRoom, prevRoom);
                    prevRoom = currRoom;
                    currRoom--;
                    g.addEdge(currRoom, prevRoom);
                    prevRoom = currRoom;
                    currRoom--;
                    int diff = -rooms[j];
                    for (int k = 0; k < diff; k++) {
                        g.addEdge(currRoom, prevRoom);
                        prevRoom = currRoom;
                        currRoom--;
                    }
                }
            }
            for (int j = 0; j < g.V; j++) {
                System.out.print((j + 1) + " ");
                for (int k = 0; k < g.adj.get(j).size(); k++) {
                    System.out.print(g.adj.get(j).get(k) + " ");
                }
                System.out.println(g.adj.get(j).size());
            }
            System.out.println();
        }
    }
}
